package Week2.day3;

public class Ex_for_star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//*정사각형 4*4
		for(int i=1; i<5; i++) { //1~4 증감 반복하는 변수 i
			
			for(int j=1; j<=5; j++) //1~5 증감 반복 변수 j
			{
				System.out.print("*"); //i행j열에 * 출력
			}
			System.out.println(); // 줄바꿈
			}
		}
	}


