package Week2.day3;

public class Ex_for_star2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 왼쪽 직삼각형
		for(int i=1; i<=5; i++) { // 1~5 증감하는 변수  i생성
			for(int j=1; j<=i; j++) // 1~i 증감하는 변수 j 생성
			{
				System.out.print("*"); // i행*j열 * 출력
			}
			System.out.println();	//줄바꿈
				
			}
		}
	}


